package class5;

public class EncapsulationMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Student st=new Student();
		
		System.out.println(st.getStudentName());
		System.out.println(st.getRollNo());
		System.out.println(st.getAge());
		System.out.println(st.getDob());
		System.out.println(st.getTamilMarks());
		System.out.println(st.getEngMarks()); 
		System.out.println(st.getMatMarks());
		System.out.println(st.getScienceMarks());
		System.out.println(st.getSocialmarks());
		System.out.println(st.getTotalMarks());
	}

}
